import React, { useState } from "react";
import { FaHome, FaUser, FaBars } from "react-icons/fa"; // Імпорт іконок
import { Link } from "react-router-dom";
import "../styles/Header.css";

const Header = () => {
  const [isMenuOpen, setMenuOpen] = useState(false); // Бургер меню

  const toggleMenu = () => {
    setMenuOpen((prevState) => !prevState);
  };

  return (
    <header className="header">
      <nav className="header__nav">
        <div className="header__nav-left">
          <Link to="/" className="header__nav-link">
            <FaHome className="header__icon" />
            <span className="header__nav-text">Home</span>
          </Link>
          <Link to="#profile" className="header__nav-link">
            <FaUser className="header__icon" />
            <span className="header__nav-text">User Profile</span>
          </Link>
          <Link to="#spells" className="header__nav-link">Заклинання</Link>
          <Link to="#monsters" className="header__nav-link">Монстри</Link>
          <Link to="#campaigns" className="header__nav-link">Кампанії</Link>
          <Link to="#dice-roller" className="header__nav-link">Кидки кубиків</Link>
        </div>
        <div className="header__nav-right">
          <button className="header__burger-menu" onClick={toggleMenu}>
            <FaBars />
          </button>
        </div>
      </nav>
      {isMenuOpen && (
        <div className="header__mobile-menu">
          <Link to="/" className="header__mobile-link" onClick={toggleMenu}>Home</Link>
          <Link to="#profile" className="header__mobile-link" onClick={toggleMenu}>User Profile</Link>
          <Link to="#spells" className="header__mobile-link" onClick={toggleMenu}>Заклинання</Link>
          <Link to="#monsters" className="header__mobile-link" onClick={toggleMenu}>Монстри</Link>
          <Link to="#campaigns" className="header__mobile-link" onClick={toggleMenu}>Кампанії</Link>
          <Link to="#dice-roller" className="header__mobile-link" onClick={toggleMenu}>Кидки кубиків</Link>
        </div>
      )}
    </header>
  );
};

export default Header;
